"""##Find the index of max value in an Numpy Array"""

import numpy as np

# Example scores representing predictions for 5 classes
scores = np.array([0.1, 0.3, 0.8, 0.5, 0.2])

class_names = ['dog', 'cat', 'rat', 'bird', 'snake']

# Find the index of the class with the highest score
best_class_index = np.argmax(scores)

# Print the index of the best class
print("Index of the best class:", best_class_index)

# Print the score of the best class
print("Score of the best class:", scores[best_class_index])

print("Name of the best class:", class_names[best_class_index])

import numpy as np

# Example 2D array representing scores for different classes across multiple samples
scores_2d = np.array([
    [0.1, 0.3, 0.8, 0.5, 0.2],
    [0.2, 0.6, 0.4, 0.9, 0.7],
    [0.5, 0.4, 0.3, 0.1, 0.6]
])

# Find the index of the class with the highest score for each sample
best_class_indices = np.argmax(scores_2d, axis=1)

# Print the indices of the best classes for each sample
print("Indices of the best classes for each sample:", best_class_indices)

# Print the scores of the best classes for each sample
for i, idx in enumerate(best_class_indices):
    # print("Sample", i+1, "- Score of the best class:", scores_2d[i, idx])
    print(f"Sample {i+1} - Score of the best class: {scores_2d[i, idx]}")

